example.screen:
resource nfs-lower-amsterdam {
         disk        /dev/volgroup/lv-nfs; 
         meta-disk   internal; 
         device      /dev/drbd0; 
         protocol    C;  
         net {
                      shared-secret  "2a9702a6-8747-11e3-9ebb-782bcbd0c11c"; 
         }

         on alice { 
                      address         192.168.201.111:7900; 
                      node-id 0; 
         }
         on bob { 
                      address         192.168.201.112:7900; 
                      node-id 1; 
         }
         connection-mesh { 
                      hosts alice bob;
         }
}

resource nfs-lower-berlin {
         disk        /dev/volgroup/lv-nfs; 
         meta-disk   internal; 
         device      /dev/drbd0; 
         protocol    C;  
         net {
                      shared-secret "2e9290a0-8747-11e3-a28c-782bcbd0c11c"; 
         }

         on charly { 
                      address         192.168.202.111:7900; 
                      node-id 0;
         }
         on doro { 
                      address         192.168.202.112:7900; 
                      node-id 1;
         }
         connection-mesh {
                      hosts charly doro;
         }
}

resource nfs-upper  {
         disk        /dev/drbd0; 
         meta-disk   internal; 
         device      /dev/drbd10; 
         protocol    A;  
         net {
                      shared-secret  "3105dd88-8747-11e3-a7fd-782bcbd0c11c";  
                      ping-timeout   20; 
         }

         stacked-on-top-of           nfs-lower-amsterdam { 
                      address        192.168.201.151:7910; 
         }
         stacked-on-top-of           nfs-lower-berlin { 
                      address        192.168.202.151:7910; 
         }
}
