step.screen:
startup {
    # wfc-timeout degr-wfc-timeout outdated-wfc-timeout
    # wait-after-sb;
    wfc-timeout 100;
    degr-wfc-timeout 120;
}

resource r0 { 
  device /dev/drbd0; 
  disk /dev/sda1; 
  meta-disk internal; 
  on alice { 
    address  192.168.1.10:7788; 
    node-id 0; 
  }
  on bob { 
    address 192.168.1.11:7788; 
    node-id 1; 
  }
  disk {
    resync-rate 10M; 
  }
  connection-mesh { 
    hosts alice bob;
  }
}

example.screen:
# /etc/drbd.d/r0.res
resource r0 {
  protocol C;
  device    /dev/drbd0;
  disk      /dev/sda1;
  meta-disk internal;

  on amsterdam-alice {
    address    192.168.1.1:7900;
  }

  on amsterdam-bob {
    address    192.168.1.2:7900;
  }
}

resource r0-U {
  protocol A;
  device     /dev/drbd10;

  stacked-on-top-of r0 {
    address    192.168.2.1:7910;
  }

  on berlin-charlie {
    disk       /dev/sda10;
    address    192.168.2.2:7910; # Public IP of the backup node
    meta-disk  internal;
  }
}
