step.screen:
resource nfs {
   volume 0 { 
      device           /dev/drbd0; 
      disk             /dev/nfs/state; 
      meta-disk        internal; 
   }
   volume 1 {
      device           /dev/drbd1;
      disk             /dev/nfs/share;
      meta-disk        internal;
   }

   net {
      protocol C; 
      fencing resource-and-stonith; 
   }

   handlers { 
      fence-peer "/usr/lib/drbd/crm-fence-peer.9.sh";
      after-resync-target "/usr/lib/drbd/crm-unfence-peer.9.sh";
   }

   connection-mesh { 
      hosts     &node1; &node2;;
   }
   on &node1; { 
      address   &subnetI;.1:&drbd.port;;
      node-id   0;
   }
   on &node2; {
      address   &subnetI;.2:&drbd.port;;
      node-id   1;
   }
}

&prompt.root;crm status
